mapper.select:
SELECT 

from timeline_label_quick a

WHERE a.id = #{id}

SELECT 

from timeline_label_quick a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name like concat('%',#{name},'%')


		AND a.type = #{type}


		AND a.labels = #{labels}




		ORDER BY #{sqlOrderBy}


		ORDER BY a.update_date DESC

SELECT 

from timeline_label_quick a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY #{sqlOrderBy}


		ORDER BY a.update_date DESC

mapper.update:

UPDATE timeline_label_quick SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
