mapper.select:
SELECT 

FROM timeline a

WHERE a.id = #{id}

SELECT 

FROM timeline a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.pic_id = #{pic.id}


		AND a.type = #{type}


		AND a.label like concat('%',#{label},'%')


		AND a.score = #{score}




		ORDER BY #{sqlOrderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM timeline a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY #{sqlOrderBy}


		ORDER BY a.update_date DESC

mapper.insert:
INSERT INTO timeline_ignore(
	pic_id,
	type
) VALUE(
	#{pic.id},
	#{type}
)

mapper.update:

UPDATE timeline SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
