mapper.sql:
a.del_flag = #{DEL_FLAG_NORMAL}

	and a.name like concat('%',#{name}, '%')


	and a.brief like concat('%',#{brief}, '%')


	and a.label like concat('%',#{label}, '%')


	and a.filetype = #{filetype}


	and a.state = #{state}

mapper.select:
select

from file_multipack a

where a.id = #{id}
