mapper.sql:
left join file_sort_list f on f.id = a.id left join ( SELECT a.pid, GROUP_CONCAT(cast(l.label as CHAR) SEPARATOR ',') as label FROM file_pdf_label_relation a LEFT JOIN file_pdf_label l ON l.id = a.labelid
GROUP BY a.pid
) label on label.pid = a.id

a.del_flag = #{DEL_FLAG_NORMAL}

	and a.bookname = #{bookname}


	and a.name = #{name}


	and a.typef = #{typef}


	and a.type = #{type}


	and a.author = #{author}


	and a.years = #{years}


	and a.grade = #{grade}


	and a.country = #{country}


	and a.translator = #{translator}


	and a.press = #{press}


	and a.state = #{state}

mapper.select:
select

from file_pdf a

where a.id = #{id}
