mapper.select:
SELECT 

FROM cost_lijin a

WHERE a.id = #{id}

SELECT 

FROM cost_lijin a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.type = #{type}


		AND a.isyihuan = #{isyihuan}


		AND a.guishuren = #{guishuren}




		ORDER BY #{sqlOrderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM cost_lijin a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY #{sqlOrderBy}


		ORDER BY a.update_date DESC

mapper.update:

UPDATE cost_lijin SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
